mapper.select:
SELECT count(*)
FROM chattingWeb.USERS
WHERE USERS.EMAIL = #{email}

SELECT count(*)
FROM chattingWeb.USERS
WHERE USERS.EMAIL = #{email}

SELECT count(*)
FROM chattingWeb.USERS
WHERE USERS.EMAIL = #{email}
  and  USERS.password = #{password}

SELECT *
FROM chattingWeb.USERS
WHERE USERS.EMAIL = #{email}
  and  USERS.password = #{password}

SELECT user_id as userId, userName as userName,
password as password ,
email as email, role as role FROM chattingWeb.USERS WHERE USERS.email = #{email}

select count(*)
from USERS
where 1=1
and   userName = #{userName}
and  email = #{email};
